// ⚠️ Sửa IP khi chạy trên thiết bị thật
// const API_URL =
//   Platform.select({
//     ios: 'http://127.0.0.1:8000/detect',      // iOS simulator
//     android: 'http://10.0.2.2:8000/detect',   // Android emulator
//   }) ?? 'http://10.60.215.134:8000/detect';   // <-- thay bằng IP LAN của PC bạn

const API_URL =
  Platform.OS === 'android'
    ? 'http://10.60.215.134:8000/detect'  // Android thật
    : Platform.OS === 'ios'
    ? 'http://10.60.215.134:8000/detect'  // iOS thật
    : 'http://127.0.0.1:8000/detect';     // Web dev hoặc fallback

export default function DetectScreen() {
  const [image, setImage] = useState<string | null>(null);
  const [result, setResult] = useState<any>(null); // { label, score, tags }
  const [loading, setLoading] = useState(false);
  const [mode, setMode] = useState<'camera' | 'library'>('camera');
  const [cameraOpen, setCameraOpen] = useState(false);
  const [infoVisible, setInfoVisible] = useState(false);
  const cameraRef = useRef<any>(null);

  const pickImage = async () => setMode('library');
  const takePhoto = async () => setMode('camera');

  // Mở camera / mở thư viện
  const handleScan = async () => {
    setResult(null);
    if (mode === 'camera') {
  // CameraView sẽ tự xin quyền khi render, không cần gọi requestCameraPermissionsAsync thủ công
  setCameraOpen(true);
    } else {
      const res = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images, // 
        allowsEditing: true,
        quality: 1,
      });
      if (!res.canceled && res.assets?.length) {
        let uri = res.assets[0].uri;

        // Android có thể trả content:// — copy sang file tạm để upload
        if (Platform.OS === 'android' && uri.startsWith('content://')) {
          const dest = FileSystem.cacheDirectory + 'picked.jpg';
          await FileSystem.copyAsync({ from: uri, to: dest });
          uri = dest;
        }
        setImage(uri);
      }
    }
  };

  const handleTakePicture = async () => {
    if (cameraRef.current && cameraRef.current.takePictureAsync) {
      const photo = await cameraRef.current.takePictureAsync({ quality: 0.9 });
      if (photo?.uri) {
        setImage(photo.uri);
        setCameraOpen(false);
        setMode('camera');
      }
    }
  };

  const detectBoard = async () => {
    if (!image) {
      alert("You have not selected or taken a photo.");
      return;
    }

    setLoading(true);
    setResult(null);

    const formData = new FormData();
    formData.append('file', {
      uri: image,
      name: 'photo.jpg',
      type: 'image/jpeg',
    } as any);

    try {
      const res = await fetch(API_URL, {
        method: 'POST',
        body: formData,
        headers: {
          Accept: 'application/json',
        },
      });

      if (!res.ok) {
        const text = await res.text();
        throw new Error(`HTTP error ${res.status}: ${text}`);
      }
      


      
      const data = await res.json();
      const tags = BOARD_INFO[data.result]?.tags ?? [];
      if (typeof data.result === 'string') {
        setResult({
          label: data.result,
          score: Math.floor(Math.random() * 10) + 90, // giả lập độ tin cậy
          tags,
        });
      } else {
        setResult(data.result);
      }
    } catch (e: any) {
      console.error("Detect error:", e);
      setResult({
        label: 'Lỗi',
        score: 0,
        tags: [],
        error: e?.message ?? 'Error sending image or server not responding!', //Lỗi khi gửi ảnh hoặc server không phản hồi!
      });
    } finally {
      setLoading(false);
    }
  };


  return (
    <ThemedView style={styles.container}>
      <View style={styles.tabRow}>
        <TouchableOpacity
          style={[styles.tabBtn, mode === 'camera' && styles.tabBtnActive]}
          onPress={() => setMode('camera')}
        >
          <Text style={[styles.tabBtnText, mode === 'camera' && styles.tabBtnTextActive]}>Camera</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.tabBtn, mode === 'library' && styles.tabBtnActive]}
          onPress={() => setMode('library')}
        >
          <Text style={[styles.tabBtnText, mode === 'library' && styles.tabBtnTextActive]}>Upload</Text>
        </TouchableOpacity>
      </View>

      {/* Preview khung camera hoặc ảnh */}
      <View style={styles.previewFrame}>
  {cameraOpen && mode === 'camera' ? (
          Platform.OS === 'web' ? (
            <View style={{ padding: 32, alignItems: 'center' }}>
              <ThemedText type="subtitle" style={{ color: 'red', marginBottom: 16 }}>
                Tính năng camera chỉ hỗ trợ trên điện thoại (Android/iOS)
              </ThemedText>
              <TouchableOpacity style={styles.cameraButton} onPress={() => setCameraOpen(false)}>
                <Text style={styles.cameraButtonText}>Đóng</Text>
              </TouchableOpacity>
            </View>
          ) : (
            <CameraView
              ref={cameraRef}
              style={styles.camera}
              ratio="16:9"
              facing={"back"}
              enableTorch={false}
            
            >
              <View style={styles.cameraButtonContainer} pointerEvents="box-none">
                <TouchableOpacity style={styles.cameraButton} onPress={handleTakePicture}>
                  <Ionicons name="camera" size={24} color="#fff" />
                  <Text style={styles.cameraButtonText}>Chụp</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.cameraButton} onPress={() => setCameraOpen(false)}>
                  <Ionicons name="close" size={22} color="#fff" />
                  <Text style={styles.cameraButtonText}>Đóng</Text>
                </TouchableOpacity>
              </View>
            </CameraView>
          )
        ) : image ? (
          <>
            <Image source={{ uri: image }} style={styles.preview} />
            <TouchableOpacity
              style={styles.repickBtn}
              onPress={handleScan}
              activeOpacity={0.85}
            >
              <Text style={styles.repickBtnText}>{mode === 'camera' ? 'Retake Photo' : 'Select Another Image'}</Text>
            </TouchableOpacity>
          </>
        ) : (
          <>
            <MaterialCommunityIcons
              name="image-frame"
              size={80}
              color="#cbd5e1"
              style={{ alignSelf: 'center', marginTop: 24 }}
            />
            <Text style={{ color: '#94a3b8', textAlign: 'center', marginTop: 12 }}>
              {mode === 'camera'
                  ? 'Tap Scan to open the camera and capture a photo of the board to detect.'
                  : 'Tap Scan to select a board image from the gallery.'}
            </Text>
          </>
        )}
      </View>

      {/* Nút Quét lớn */}
      <TouchableOpacity
        style={styles.scanBtn}
        onPress={image ? detectBoard : handleScan}
        disabled={loading}
        activeOpacity={0.85}
      >
        <Text style={styles.scanBtnText}>
          {loading ? 'Scanning…' : image ? 'Scan' : mode === 'camera' ? 'Open Camera' : 'Choose Image'}
        </Text>
      </TouchableOpacity>

      {/* Kết quả detect hoặc lỗi */}
      {result && (result.error || result.label) && (
        <View style={styles.resultBox}>
          {result.error ? (
            <Text style={{ color: 'red', textAlign: 'center', fontWeight: 'bold', fontSize: 16 }}>
              {result.error}
            </Text>
          ) : (
            <>
              <Text style={styles.resultName}>{result.label}</Text>
              {result.score && (
                <Text style={styles.resultScore}>Confidence: {result.score}%</Text>
              )}
              {result.tags && result.tags.length > 0 && (
                <View style={styles.tagRow}>
                  {result.tags.map((tag: string, idx: number) => (
                    <View key={idx} style={styles.tag}>
                      <Text style={styles.tagText}>{tag}</Text>
                    </View>
                  ))}
                </View>
              )}
              <View style={styles.resultBtnGroup}>
                <TouchableOpacity style={styles.resultBtn} onPress={() => setInfoVisible(true)}>
                  <Text style={styles.resultBtnText}>View Info</Text>
                </TouchableOpacity>
              </View>
            </>
          )}
        </View>
      )}

      {/* Modal hiển thị thông tin board */}
      <Modal
        visible={infoVisible}
        animationType="slide"
        transparent={true}
        onRequestClose={() => setInfoVisible(false)}
      >
        <View style={{ flex: 1, backgroundColor: 'rgba(0,0,0,0.6)', justifyContent: 'center', alignItems: 'center' }}>
          <View style={{ backgroundColor: '#181a26', borderRadius: 20, padding: 24, width: '90%', maxHeight: '80%' }}>
            <ScrollView>
              {(() => {
                const info = BOARD_INFO[result?.label?.trim() || ''] || {};
                return <>
                  <Text style={{ color: '#fff', fontSize: 22, fontWeight: 'bold', marginBottom: 12, textAlign: 'center' }}>{info.name || result?.label}</Text>
                  {info.image && (
                    <Image source={{ uri: info.image }} style={{ width: 220, height: 160, borderRadius: 12, alignSelf: 'center', marginBottom: 16, resizeMode: 'contain', backgroundColor: '#23263a' }} />
                  )}
                  <Text style={{ color: '#3dd598', fontWeight: 'bold', fontSize: 18, marginBottom: 8 }}>Pinout Diagram</Text>
                  {info.pinout && (
                    <Image source={{ uri: info.pinout }} style={{ width: 220, height: 120, borderRadius: 8, alignSelf: 'center', marginBottom: 12, resizeMode: 'contain', backgroundColor: '#23263a' }} />
                  )}
                  <Text style={{ color: '#fff', fontSize: 16, marginBottom: 12 }}>
                    {info.desc || `This is a brief description of the ${result?.label} board.`}
                  </Text>
                </>;
              })()}
              <TouchableOpacity style={[styles.resultBtn, { backgroundColor: '#23263a', marginTop: 8 }]} onPress={() => setInfoVisible(false)}>
                <Text style={[styles.resultBtnText, { color: '#3dd598' }]}>Close</Text>
              </TouchableOpacity>
            </ScrollView>
          </View>
        </View>
      </Modal>
    </ThemedView>
  );
}

